class Typewriter{constructor(e,t){const n={cursorDelay:500,typingDelay:300,deleteDelay:200};this.settings={...n,...t},this.targetSelector=e}async type(e){const t=document.querySelector(this.targetSelector);let n=0;await new Promise(s=>{let l=setInterval(()=>{t.innerHTML+=e[n],n++,n===e.length&&(s(()=>this),clearInterval(l))},this.settings.typingDelay)})}async delete(e){const t=document.querySelector(this.targetSelector);let n=0;await new Promise(s=>{let l=setInterval(()=>{t.innerHTML=t.innerHTML.slice(0,-1),n++,n===e&&(s(),clearInterval(l))},this.settings.deleteDelay)})}blink(){const e=document.querySelector(this.targetSelector);let t=document.createElement("span");t.innerHTML="|",e.append(t),setInterval(()=>{t.classList.toggle("hidden")},this.settings.cursorDelay)}}const t=new Typewriter(".motto");t.type("Minimal and").then(()=>t.delete(3)).then(()=>t.type("IS effective")).then(()=>t.blink());